{#if label}
  <label>
    {label}
    <input type="radio"
           {value}
           {...$$restProps}
           bind:group
           on:focus
           on:blur
           on:change />
  </label>
{:else}
  <input type="radio"
         {value}
         {...$$restProps}
         bind:group
         on:focus
         on:blur
         on:change />
{/if}

<script >export let label = '';
export let value = '';
export let group = '';
</script>
